                default xml namespace = "urn:hl7-org:v3";
                xcpd = new XML(xcpd.toString());

                soap.*::Body.setChildren(xcpd);
                channelMap.put('SOAP', soap.toString());

// Transmission Wrapper settings
                  channelMap.put('ID_EXT', msg.id.toString());
                  channelMap.put('ID_ROOT', $cfg('HL7v3.Sender.OID'));
                  channelMap.put('CREATION_TIME',
