com.mirth.connect.plugins.javascriptstep.script:
var json = null, error = null;

// HTTP 400 Bad Request - by default, the server cannot or will not process the request
channelMap.put("responseCode", "400");


// Destination is not needed
destinationSet.removeAll();


// Decode and parse incoming JSON request
json = getBase64Content(msg, 'json');


// Validate request
if ('string' == typeof json) {
	error = 'ERROR - ' + ex;
} else {	

	// HTTP 202 Accepted - the request is an array of entries and has been accepted for further processing
	if (Array.isArray(json)) { 
		channelMap.put("responseCode", "202");
	} else {		
		error = 'ERROR - Incoming feed is not an array of entries';
	}
}


// Cease processing in case of an error
if (error) {
	channelMap.put('NOTE', error);
	throw error;
}

var	totalCount = 0, 
	processedCount = 0;

// Loop through all request entries
if (json) {
	json.forEach(function(entry) {	

		totalCount++;
		var errorCount = 0;

		// Check for required parameters
		try {

			if (!entry.hasOwnProperty('urlXCPD') || 0 == entry.urlXCPD.toString().length) {
				errorCount++
			} else if (!entry.hasOwnProperty('urlDQ') || 0 == entry.urlXCPD.toString().length) {
				errorCount++
			} else if (!entry.hasOwnProperty('urlDR') || 0 == entry.urlXCPD.toString().length) {
				errorCount++
			}

		} catch(ex) {errorCount++}

		// Pass to the XCPD Interface channel to process
		if (0 == errorCount) {

			var result = router.routeMessageByChannelId($g('XCAITI38Interface'), JSON.stringify(entry, null, 2));
// TODO: Verify response and increase processedCount

		} else {
			channelMap.put('NOTE', 'ERROR - ' + errorCount.toString() + ' error(s) is/are found in the entries');
		}
	});
}

// Store for stat
channelMap.put('TCOUNT', totalCount.toString());
channelMap.put('PCOUNT', processedCount.toString());

