com.mirth.connect.plugins.javascriptstep.script:
var json = null, error = null;


// HTTP 400 Bad Request - by default, the server cannot or will not process the request
channelMap.put("responseCode", "400");


// Decode and parse incoming JSON request if sent over HTTP
if (msg.toString().startsWith('<HttpRequest>')) {
	json = getBase64Content(msg, 'json');
} else try {
	// Parse JSON request if routed from the XCPD Bulk Interface channel
	json = JSON.parse(msg.toString());
} catch(ex) {
	error = 'ERROR - ' + ex;
}


// If decoding and parsing the HTTP request fail, it returns an error as a string
if ('string' == typeof json) {
	error = 'ERROR - ' + ex;


} else try {	

	// HTTP 202 Accepted - the request is a single entry and has been accepted for further processing
	if (!Array.isArray(json)) {

		// Set HTTP response code and processing statistics
		channelMap.put("responseCode", "202");		
		channelMap.put('MSG_ID', json.id.toString());
		channelMap.put('CUSTOMER_ID', json.cxId.toString());

	} else {
		error = 'ERROR - Incoming feed is an array of entries';
	}
} catch(ex) {
	error = 'ERROR - ' + ex;
}


// Cease processing in case of an error
if (error) {
	channelMap.put('NOTE', error);
	throw error;
}

var xcpdArray = json.xcpdGateways;
delete json.xcpdGateways;

var errorCount = 0;

// Generate XCPD ITI-55 Processor feed for each entry
xcpdArray.forEach(function(entry) { 

	try {

		var jsonXCPD = json;
		jsonXCPD.id = entry.id.toString();

		if (entry.hasOwnProperty('oid') && entry.oid.toString().length > 0) {
			jsonXCPD.gatewayOID = entry.oid.toString();
		} else {
			errorCount++;
		}

		if (entry.hasOwnProperty('url') && entry.url.toString().length > 0) {
			jsonXCPD.urlXCPD = entry.url.toString();
		} else {
			errorCount++;
		}

		if (0 == errorCount) {
			var result = router.routeMessageByChannelId($g('ITI55PROCESSOR'), JSON.stringify(jsonXCPD));
		} else {
			channelMap.put('NOTE', 'ERROR - ' + errorCount.toString() + ' error(s) is/are found in the entries');
		}

	} catch(ex) {
		logger.error('XCPD Interface exception: ' + ex);
	}
});

// Destination is not needed
destinationSet.removeAll();

channel.postprocessingScript:
// This script executes once after a message has been processed
// Responses returned from here will be stored as "Postprocessor" in the response map
return;

channel.deployScript:
