/**
	Generates XCPD Cross Gateway Patient Discovery [ITI-55] request template

	@param {String} oid - an OID of the sending organization
	@param {Object} receiver - an XML object with the receiver information
	@param {Object} sender - an XML object with the sender information
	@return {Object} return XCPD ITI-55 request payload
*/
function getXCPDRequest(oid, receiver, sender) {

	// queryId - Identifier for the query. It is used to associate this query instance with both the initial response message and with later query interactions. (REQUIRED)
	// Valuing queryId avoids the need for the QueryContinuation and QueryAck classes to carry as much detail information as is carried in the initial query.

	var uuid = UUIDGenerator.getUUID();

	var xml = <PRPA_IN201305UV02 ITSVersion="XML_1.0" xmlns:urn="urn:hl7-org:v3">
				<id root={oid.toString()} extension={uuid.toString()}/>
				<creationTime value={DateUtil.getCurrentDate('yyyyMMddHHmmss')}/>
				<interactionId root="2.16.840.1.113883.1.6" extension="PRPA_IN201305UV02"/>
				<processingCode code="P"/>
				<processingModeCode code="T"/>
				<acceptAckCode code="AL"/>
				<controlActProcess classCode="CACT" moodCode="EVN">
					<urn:code code="PRPA_TE201305UV02" codeSystem="2.16.840.1.113883.1.6"/>
					<urn:queryByParameter>
						<urn:queryId root={oid.toString()} extension={uuid.toString()}/>
						<urn:statusCode code="new"/>
						<urn:responseModalityCode code="R"/>
						<urn:responsePriorityCode code="I"/>
						<urn:parameterList/>
					</urn:queryByParameter>
				</controlActProcess>
			</PRPA_IN201305UV02>;

	// Sender shall be inserted first to preserve the order
	if (sender) xml.insertChildAfter(xml.acceptAckCode[0], sender);
	if (receiver) xml.insertChildAfter(xml.acceptAckCode[0], receiver);

	return xml;
}

/**
	Generate livingSubjectAdministrativeGender query entry
	This query parameter is a code representing the administrative gender of a living subject in a patient registry

	@param {Object} patient - incoming Patient resource
	@return {Object} return XML object representing LivingSubjectAdministrativeGender query entry
*/
function getLivingSubjectAdministrativeGender(patient) {

	// 'Undifferentiated' by default
	var gender = 'UN';

	try {
		if (String(patient.gender).toUpperCase().startsWith('M')) gender = 'M'
		else if (String(patient.gender).toUpperCase().startsWith('F')) gender = 'F'
	} catch(ex) {}

	var xml = <urn:livingSubjectAdministrativeGender xmlns:urn="urn:hl7-org:v3">
				<urn:value code={gender.toString()} codeSystem="2.16.840.1.113883.5.1"/>
				<urn:semanticsText>LivingSubject.administrativeGender</urn:semanticsText>
			</urn:livingSubjectAdministrativeGender>;

	return xml;
}

/**
	Generate LivingSubjectBirthPlaceAddress query entry
	This query parameter is a patient's birthplace represented as an address

	@param {Object} address - birth place Address object from the extension
	@return {Object} return XML object representing LivingSubjectBirthPlaceAddress query entry
*/
function getLivingSubjectBirthPlaceAddress(address) {

	var xml = null;

	try {

		xml = <urn:livingSubjectBirthPlaceAddress xmlns:urn="urn:hl7-org:v3">
		          <urn:value/>
		          <urn:semanticsText>LivingSubject.BirthPlace.Addr</urn:semanticsText>
		        </urn:livingSubjectBirthPlaceAddress>;

		address.line.forEach(line => {
			var street = <urn:streetAddressLine xmlns:urn="urn:hl7-org:v3">{line.toString()}</urn:streetAddressLine>;
			xml.*::value.appendChild(street);
		});
		if (address.hasOwnProperty('city')) {
			var city = <urn:city xmlns:urn="urn:hl7-org:v3">{address.city.toString()}</urn:city>;
			xml.*::value.appendChild(city);
		}
		if (address.hasOwnProperty('state')) {
			var state = <urn:state xmlns:urn="urn:hl7-org:v3">{address.state.toString()}</urn:state>;
			xml.*::value.appendChild(state);
		}
		if (address.hasOwnProperty('postalCode')) {
			var postalCode = <urn:postalCode xmlns:urn="urn:hl7-org:v3">{address.postalCode.toString()}</urn:postalCode>;
			xml.*::value.appendChild(postalCode);
		}
		if (address.hasOwnProperty('country')) {
			var country = <urn:country xmlns:urn="urn:hl7-org:v3">{address.country.toString()}</urn:country>;
			xml.*::value.appendChild(country);
		}

	} catch(ex) {}

	return xml;
}

