}

/**
	Generate LivingSubjectBirthTime query entry
	This query parameter is the birth date of a living subject.

	@param {String} date - Patient's birth date separated by '-'
	@return {Object} return XML object representing LivingSubjectBirthTime query entry
*/
function getLivingSubjectBirthTime(date) {

	var xml = null;

	try {
		xml = <urn:livingSubjectBirthTime xmlns:urn="urn:hl7-org:v3">
				<urn:value value={date.toString().replace(/-/g, '')}/>
				<urn:semanticsText>LivingSubject.birthTime</urn:semanticsText>
			</urn:livingSubjectBirthTime>;		
	} catch(ex) {}

	return xml;

}

/**
	Generate LivingSubjectId query entry
	This query parameter is an identifier for a living subject. This is NOT the identifier assigned by the target registry. 
	In fact, the identifier has no context (scoping organization) other than the namespace from which the identifier was issued (OID root).

	@param {Object} patient - incoming Patient resource
	@return {Object} return XML object representing LivingSubjectId query entry
*/
function getLivingSubjectId(patient) {

	var xml = null;
	var valueList = new XMLList();

	if (patient.hasOwnProperty('identifier')) try {

		patient.identifier.forEach(function(entry) {

			if (entry.system.toString().startsWith('urn:oid:')) {
				valueList += <urn:value xmlns:urn="urn:hl7-org:v3" root={entry.system.toString().replace('urn:oid:', '')} extension={entry.value.toString()}/>;

			} else if ('http://hl7.org/fhir/sid/us-ssn' == entry.system.toString()) {
				valueList += <urn:value xmlns:urn="urn:hl7-org:v3" root="2.16.840.1.113883.4.1" extension={entry.value.toString()}/>;

			} else if ('http://hl7.org/fhir/sid/us-medicare' == entry.system.toString()) {
				valueList += <urn:value xmlns:urn="urn:hl7-org:v3" root="2.16.840.1.113883.4.572" extension={entry.value.toString()}/>;

			} else if ('http://hl7.org/fhir/sid/us-mbi' == entry.system.toString()) {
				valueList += <urn:value xmlns:urn="urn:hl7-org:v3" root="2.16.840.1.113883.4.927" extension={entry.value.toString()}/>;
			}			
		});

	} catch(ex) {}

	if (valueList.length() > 0) {
		xml = <urn:livingSubjectId xmlns:urn="urn:hl7-org:v3">
				<urn:semanticsText>LivingSubject.id</urn:semanticsText>
			</urn:livingSubjectId>;		
		xml.insertChildBefore(xml.*::semanticsText[0], valueList);
	}

	return xml;

}

/**
	Generate LivingSubjectName query entry
	This query parameter is the name of a living subject. This query parameter is the name of a person. 
	If multiple instances of LivingSubjectName are provided, the receiver must consider them as possible alternatives, logically connected with an "or".

	@param {Object} name - a single Patient.name element
	@return {Object} return XML object representing LivingSubjectName query entry
*/
function getLivingSubjectName(name) {

	var xml = null;

	// Only one instance of the value element is allowed. Only some of the name parts may be populated. 
	// If, for example, only the family and given name parts of a person's name are sent, then the query would match all persons with that family name and given name regardless of their initials. 
	// The use attribute of the value element shall not be set to "SRCH".

	try {

		xml = <urn:livingSubjectName xmlns:urn="urn:hl7-org:v3">
				<urn:value/>	
				<urn:semanticsText>LivingSubject.name</urn:semanticsText>
			</urn:livingSubjectName>;

		if (name.hasOwnProperty('family')) {
			var family = <urn:family xmlns:urn="urn:hl7-org:v3">{name.family.toString()}</urn:family>;
			xml.*::value.appendChild(family);
		}

		// TODO: Process multiple given names (requires XMLList)		
		if (name.hasOwnProperty('given')) {
			var given = <urn:given xmlns:urn="urn:hl7-org:v3">{name.given[0].toString()}</urn:given>;
			xml.*::value.appendChild(given);
/*			
			name.given.forEach(entry => {
				var given = <given>{name.given.toString()}</given>;
				xml.value.appendChild(given);
			});
*/			
		}

	} catch(ex) {}

	return xml;
}

/**
	This query parameter is the maiden name of a focal person's mother. 
	It is included as a parameter because it is a common attribute for confirming the identity of persons in some registries.
	In this case it may consist of only the given name part, the family name part, or both.

	@param {Object} name - a mother's maiden name
	@return {Object} return XML object representing LivingSubjectName query entry
*/
function getMothersMaidenName(name) {

	// The FHIR http://hl7.org/fhir/StructureDefinition/patient-mothersMaidenName extension allows only a single string value
	var xml = <urn:mothersMaidenName xmlns:urn="urn:hl7-org:v3">
			     <urn:value>{name.toString()}</urn:value>
			     <urn:semanticsText>Person.MothersMaidenName</urn:semanticsText>
			   </urn:mothersMaidenName>;

	return xml;
}

/**
	Generates XCPD response based on the initial request and OperationOutcome

	@param {Object} request - Initial JSON request
	@param {Object} operationOutcome - FHIR OperationOutcome resource instance
	@return {Object} return JSON response
*/
function getOperationOutcomeResponse(request, operationOutcome) {

	var result = request;
	result.patientResource = 'Patient/' + channelMap.get('PATIENT_ID');
	result.patientMatch = false;
	result.processingTimestamp = DateUtil.getCurrentDate("yyyy-MM-dd'T'hh:mm:ss.sssZ");
	result.operationOutcome = operationOutcome;

	return result;	
}

/**
	Generates PatientAddress query entry
	This query parameter is a postal address for corresponding with a living subject in the context of the target patient registry.
	There shall be only a single PatientAddress element.
	Multiple instances of the value element within a Patient Address may be specified and are combined with OR logic.

	@param {Object} address - Patient.address element
	@return {Object} return XML object representing PatientAddress query entry
*/
function getPatientAddress(address) {

	var xml = null;
	var valueList = new XMLList();

	try {

		address.forEach(function(addr) {

			var value = <urn:value xmlns:urn="urn:hl7-org:v3"/>;

			addr.line.forEach(line => {
				var street = <urn:streetAddressLine xmlns:urn="urn:hl7-org:v3">{line.toString()}</urn:streetAddressLine>;
				value.appendChild(street);
			});	
			if (addr.hasOwnProperty('city')) {
				var city = <urn:city xmlns:urn="urn:hl7-org:v3">{addr.city.toString()}</urn:city>;
				value.appendChild(city);
			}
			if (addr.hasOwnProperty('state')) {
				var state = <urn:state xmlns:urn="urn:hl7-org:v3">{addr.state.toString()}</urn:state>;
				value.appendChild(state);
			}
			if (addr.hasOwnProperty('postalCode')) {
				var postalCode = <urn:postalCode xmlns:urn="urn:hl7-org:v3">{addr.postalCode.toString()}</urn:postalCode>;
				value.appendChild(postalCode);
			}
			if (addr.hasOwnProperty('country')) {
				var country = <urn:country xmlns:urn="urn:hl7-org:v3">{addr.country.toString()}</urn:country>;
				value.appendChild(country);
			}
			valueList += value;
		});

	} catch(ex) {}

	if (valueList.length() > 0) {
		xml = <urn:patientAddress xmlns:urn="urn:hl7-org:v3">
				<urn:semanticsText>Patient.addr</urn:semanticsText>
			</urn:patientAddress>;
		xml.insertChildBefore(xml.*::semanticsText[0], valueList);
	}

	return xml;
}

/**
	Generates PatientTelecom query entry
	This query parameter is a telecommunications address for communicating with a living subject in the context of the target patient registry. 
	It could be a telephone number, fax number or even an email address. There shall be only a single PatientTelecom element.
	A telecommunications address. The scheme attribute specifies whether this is a telephone number, fax number, email address, etc. 
	Multiple instances of the value element within a PatientTelecom may be specified and are combined with OR logic.

	@param {Object} telecom - Patient.telecom element
	@return {Object} return XML object representing PatientTelecom query entry
*/
function getPatientTelecom(telecom) {

	// TODO: The FHIR ContactPoint.use component is not being processed

	var xml = null;
	var valueList = new XMLList();

	try {
		telecom.forEach(function(tel) {

			var prefix = '';

			if (tel.hasOwnProperty('system')) {
				// Map FHIR ContactPoint.system to HL7v3 syntax
				var prefix = ['tel:','fax:','email:','','','sms:',''][['phone','fax','email','pager','url','sms','other'].indexOf(tel.system.toString())];
				prefix = (prefix) ? prefix : '';
			}

			var value = <urn:value xmlns:urn="urn:hl7-org:v3" value={prefix.toString() + tel.value.toString()}/>;			
			valueList += value;

		});

	} catch(ex) {}

	if (valueList.length() > 0) {
		xml = <urn:patientTelecom xmlns:urn="urn:hl7-org:v3">
				<urn:semanticsText>Patient.telecom</urn:semanticsText>
			</urn:patientTelecom>;
		xml.insertChildBefore(xml.*::semanticsText[0], valueList);
	}

	return xml;
}

/**
	Generates 
	This query parameter is the identifier of a health care provider who has explicitly accepted responsibility for some aspect of a patient's care.
	This identifies a health care provider role.

	@param {String} id - provider's NPI number
	@return {Object} return XML object representing PrincipalCareProviderId query entry
*/
function getPrincipalCareProviderId(id) {

	// NOTE: Only provider's NPI numbers are expected

	var xml = null;

	try {
		// 2.16.840.1.113883.4.6 is US NPI OID
		xml = <urn:principalCareProviderId xmlns:urn="urn:hl7-org:v3">
		          <urn:value root="2.16.840.1.113883.4.6" extension={id.toString()}/>
		        </urn:principalCareProviderId>;        
	} catch(ex) {}


	return xml;

}

